import React, { Component } from 'react';
import Counter from './counter';

class Counters extends Component {
    state = { counters : [{id: 1, value: 0}, {id: 2, value: 0},{id: 3, value: 0},{id: 4, value: 0}] }
    handleIncrement = (cId) => {
        const newCounters = this.state.counters;
        newCounters.forEach(function(item) {if(item.id===cId) item.value++;});
        this.setState( {counters :newCounters});
    };
    handleDecrement = (cId) => {
        const newCounters = this.state.counters;
        newCounters.forEach(function(item) { if(item.value>0) if(item.id===cId) item.value--;} );
        this.setState( {counters :newCounters});
    };
    handleDelete = (cId) => {
        const newCounters = this.state.counters.filter((ccc) => (ccc.id !== cId)? ccc : null);
        this.setState( {counters :newCounters});
    }
    handleDeleteAll = () => {
        const newCounters = [];
        this.setState({counters : newCounters});
    }
    render() { 
        return (
           <section>
                <div>
                   <span className="btn btn-primary">Sum: </span>
                   <button onClick={ () => this.props.onIncrement(this.props.counterId)} className="btn btn-secondary m-2">Reset</button>
                   <button onClick={ () => this.props.onIncrement(this.props.counterId)} className="btn btn-success m-2">Add</button>
                   <button onClick={ () => this.handleDeleteAll()} className="btn btn-danger m-2">DeleteAll</button>
                </div> 
            <div>
                {this.state.counters.map((c)=>
                    <Counter key={c.id} counterId = {c.id} counterValue={c.value} 
                    onDelete = {this.handleDelete}
                    onIncrement = {this.handleIncrement}
                    onDecrement={this.handleDecrement}
                    onDeleteAll={this.handleDeleteAll}
                    />)}
            </div>
           </section>            
        );
    }
}
 
export default Counters;
