import React, { Component } from 'react';

class Counter extends Component {
    state = {
        count: 0,
        counterMin: 0,
        counterMax: 10
    };
    // constructor(){
    //     super();
    //     this.handleIncrement.bind(this);
    // }
    handleIncrement = product => {
        if(this.state.count < this.state.counterMax) this.setState({count: this.state.count + 1});
    };
    handleDecrement = product => {
        if(this.state.count > this.state.counterMin) this.setState({count: this.state.count - 1});
    };
    handleDelete = count => {
        this.setState({count: count});
    }
    render() { 
        return ( 
        <div>
            <span className={this.getBadgeClasses()}>{this.formatCount()}</span>
            <button onClick={ () => this.handleIncrement()} className="btn btn-success m-2">Increment</button>
            <button onClick={ () => this.handleDecrement()} className="btn btn-info m-2">Decrement</button>
            <button onClick={ () => this.handleDelete()} className="btn btn-danger m-2">Delete</button>
        </div>
        );
    }
    getBadgeClasses() {
        let classes = "badge m-2 badge-";
        classes += (this.state.count === this.state.counterMin) ? "warning" : (this.state.count === this.state.counterMax) ? "warning" : "primary";        
        return classes;
    }

    formatCount(){
        const { count } = this.state;
        return count === this.state.counterMin ? "Zero" : count === this.state.counterMax ? "Max" : count;
        
    }
}
 
export default Counter;
