import React, {Component} from 'react';
import TableHeader from './common/tableHeader';
import Like from "./common/react";
import TableBody from './common/tableBody';

class MoviesTable extends Component {
  columns = [
    { path : 'title', label: 'Title'},
    { path : 'genre.name', label: 'Genre'},
    { path : 'numberInStock', label: 'Stock'},
    { path : 'dailyRentalRate', label: 'Rate'},
    { key: 'like',content : <Like liked={movie.liked} onClick={() => onLike(movie)}/>},
    { key: 'delete', content: <button
    onClick={() => onDelete(movie)}
    className="btn btn-danger btn-sm"
  >
    Delete
  </button>}
  ];
  render() { 
    const { movies,onDelete,onLike,onSort,sortColumn} = this.props;
    return (         
    <table className="table">
    <TableHeader columns={this.columns} sortColumn={sortColumn} onSort={onSort} />
    <TableBody data={movies} columns={this.columns}/>
    <tbody>
      {movies.map((movie) => (
        <tr key={movie._id}>
          <td className="table-title">{movie.title}</td>
          <td className="standart">{movie.genre.name}</td>
          <td className="standart">{movie.numberInStock}</td>
          <td className="standart">{movie.dailyRentalRate}</td>
          <td className="standart"></td>
          <td></td>
        </tr>
      ))}
    </tbody>
  </table>);
  }
} 
 
export default MoviesTable;
