import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService";
import Pagination from "./common/pagination";
import Like from "./common/react";
import "../movies.css";
class Movies extends Component {
  state = {
    movies: getMovies(),
    currentPage: 1,
    pageSize:4
  };
  handleDelete = (movie) => {
    const movies = this.state.movies.filter((m) => m._id !== movie._id);
    this.setState({ movies: movies });
  };
  handleClick = (movie) => {
    const movies = [...this.state.movies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movies[index] };
    movies[index].liked = !movies[index].liked;
    this.setState({ movies });
  };
  handleReset = () => {
    this.setState({ movies: getMovies() });
  };
  handlePageChange = page =>{
    this.setState({currentPage:page});
  }
  render() {
    const { length: count } = this.state.movies;
    const { pageSize,currentPage} = this.state;
    if (count === 0)
      return (
        <div>
          <span>There are no movies in the database </span>
          <button
            onClick={() => this.handleReset()}
            className="btn btn-warning m-2"
          >
            Reset
          </button>
        </div>
      );
    return (
      <React.Fragment>
        <span>Showing {count} movies in the database </span>
        <button
          onClick={() => this.handleReset()}
          className="btn btn-warning m-2"
        >
          Reset
        </button>
        <table className="table">
          <thead>
            <tr>
              <th className="table-title">Title</th>
              <th className="standart">Genre</th>
              <th className="standart">Stock</th>
              <th className="standart">Rate</th>
              <th className="standart">React</th>
              <th className="standart"></th>
            </tr>
          </thead>
          <tbody>
            {this.state.movies.map((movie) => (
              <tr key={movie._id}>
                <td className="table-title">{movie.title}</td>
                <td className="standart">{movie.genre.name}</td>
                <td className="standart">{movie.numberInStock}</td>
                <td className="standart">{movie.dailyRentalRate}</td>
                <td className="standart">
                  <Like
                    liked={movie.liked}
                    onClick={() => this.handleClick(movie)}
                  />
                </td>
                <td>
                  <button
                    onClick={() => this.handleDelete(movie)}
                    className="btn btn-danger btn-sm"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <Pagination 
        itemsCount={count} 
        pageSize={pageSize} 
        currentPage={currentPage} 
        onPageChange={this.handlePageChange} 
        />
      </React.Fragment>
    );
  }
}

export default Movies;
