import React, {Component} from 'react';
import Like from "./common/react";

class MoviesTable extends Component {
  raiseSort = path => {
    const sortColumn = {...this.props.sortColumn};
    if(sortColumn.path === path){
      sortColumn.order = (sortColumn.order === 'asc') ? 'desc' : 'asc';
    }else{
       sortColumn.path = path;
       sortColumn.order = 'asc';
     }
     this.props.onSort(sortColumn);
  }
  render() { 
    const { movies,onDelete,onLike} = this.props;
    return (         
    <table className="table">
    <thead>
      <tr>
        <th onClick={() => this.raiseSort('title')} className="table-title">Title</th>
        <th onClick={() => this.raiseSort('genre.name')} className="standart">Genre</th>
        <th onClick={() => this.raiseSort('numberInStock')} className="standart">Stock</th>
        <th onClick={() => this.raiseSort('dailyRentalRate')} className="standart">Rate</th>
        <th className="standart">React</th>
        <th className="standart"></th>
      </tr>
    </thead>
    <tbody>
      {movies.map((movie) => (
        <tr key={movie._id}>
          <td className="table-title">{movie.title}</td>
          <td className="standart">{movie.genre.name}</td>
          <td className="standart">{movie.numberInStock}</td>
          <td className="standart">{movie.dailyRentalRate}</td>
          <td className="standart">
            <Like
              liked={movie.liked}
              onClick={() => onLike(movie)}
            />
          </td>
          <td>
            <button
              onClick={() => onDelete(movie)}
              className="btn btn-danger btn-sm"
            >
              Delete
            </button>
          </td>
        </tr>
      ))}
    </tbody>
  </table>);
  }
} 
 
export default MoviesTable;
