import React, { Component } from 'react';
class Counter extends Component {
    state = {
        count: 0,
        counterMin: 0,
        counterMax: 10,
        counterSum: 0
    };
    render() {  
        return (
            <section>
                <div>
                   <span>Sum:</span>
                   <button onClick={ () => this.props.onIncrement(this.props.counterId)} className="btn btn-success m-2">Reset</button>
                   <button onClick={ () => this.props.onIncrement(this.props.counterId)} className="btn btn-success m-2">Add</button>
                   <button onClick={ () => this.props.onIncrement(this.props.counterId)} className="btn btn-success m-2">DeleteAll</button>
                </div>
                <div>
                    <span className={this.getBadgeClasses()}>{this.formatCount()}</span>
                    <button onClick={ () => this.props.onIncrement(this.props.counterId)} className="btn btn-success m-2">Increment</button>
                    <button onClick={ () => this.props.onDecrement(this.props.counterId)} className="btn btn-info m-2">Decrement</button>
                    <button onClick={ () => this.props.onDelete(this.props.counterId)} className="btn btn-danger m-2">Delete</button>
              </div>
            </section>
        );
    }
    getBadgeClasses() {
        let classes = "badge m-2 badge-";
        classes += (this.state.count === this.state.counterMin) ? "warning" : (this.state.count === this.state.counterMax) ? "warning" : "primary";        
        return classes;
    }
     formatCount(){
        //  if(this.props.counterValue < this.state.counterMin )  this.props.counterValue = this.state.counterMin;
        //  if(this.props.counterValue > this.state.counterMax )  this.props.counterValue = this.state.counterMax;
         return this.props.counterValue === this.state.counterMin ? "Zero" : this.props.counterValue;
     }
}
 
export default Counter;
